defmodule Test2 do
  @input ["",
    "",
    "",
    "",
    ""]

  def find_digit(instructions, acc = [last_digit | _], digit_finder) do
    new_digit = instructions
    |> to_charlist
    |> Enum.reduce(last_digit, digit_finder)

    [new_digit | acc]
  end

  @doc """
  1 2 3
  4 5 6
  7 8 9

  iex> Test2.simple_pad ["ULL", "RRDDD", "LURDL", "UUUUD"]
  "1985"

  iex> Test2.simple_pad ["", "", "", "",""]
  "82958"
  """
  def simple_pad(input) do
    [_start | code] = input
    |> Enum.reduce([5], fn digit, acc ->
      find_digit(digit, acc, &find_simple_pad_digit/2)
    end)
    |> Enum.reverse
    Enum.join(code, "")
  end

  defp find_simple_pad_digit(?U, num) when num in [1,2,3], do: num
  defp find_simple_pad_digit(?R, num) when num in [3,6,9], do: num
  defp find_simple_pad_digit(?D, num) when num in [7,8,9], do: num
  defp find_simple_pad_digit(?L, num) when num in [1,4,7], do: num
  defp find_simple_pad_digit(?U, num), do: num - 3
  defp find_simple_pad_digit(?R, num), do: num + 1
  defp find_simple_pad_digit(?D, num), do: num + 3
  defp find_simple_pad_digit(?L, num), do: num - 1

  @doc """
      1
    2 3 4
  5 6 7 8 9
    A B C
      D

  iex> Test2.complex_pad ["ULL", "RRDDD", "LURDL", "UUUUD"]
  "5DB3"

  iex> Test2.complex_pad ["", "", "", "",""]
  "B3DB8"
  """
  def complex_pad(input) do
    [_start | code] = input
    |> Enum.reduce([5], fn digit, acc ->
      find_digit(digit, acc, &find_complex_pad_digit/2)
    end)
    |> Enum.reverse
    Enum.join(code, "")
  end

  @doc """
      1
    2 3 4
  5 6 7 8 9
    A B C
      D
  """
  def find_complex_pad_digit(?U, digit) when digit in [1, 2, 4, 5, 9], do: digit
  def find_complex_pad_digit(?U, 3), do: 1
  def find_complex_pad_digit(?U, digit) when is_number(digit), do: digit - 4
  def find_complex_pad_digit(?U, "A"), do: 6
  def find_complex_pad_digit(?U, "B"), do: 7
  def find_complex_pad_digit(?U, "C"), do: 8
  def find_complex_pad_digit(?U, "D"), do: "B"

  def find_complex_pad_digit(?D, digit) when digit in [5, "A", "D", "C", 9], do: digit
  def find_complex_pad_digit(?D, "B"), do: "D"
  def find_complex_pad_digit(?D, 1), do: 3
  def find_complex_pad_digit(?D, digit) when digit in [2, 3, 4], do: digit + 4
  def find_complex_pad_digit(?D, 6), do: "A"
  def find_complex_pad_digit(?D, 7), do: "B"
  def find_complex_pad_digit(?D, 8), do: "C"

  def find_complex_pad_digit(?R, digit) when digit in [1, 4, 9, "C", "D"], do: digit
  def find_complex_pad_digit(?R, digit) when is_number(digit), do: digit + 1
  def find_complex_pad_digit(?R, "A"), do: "B"
  def find_complex_pad_digit(?R, "B"), do: "C"

  def find_complex_pad_digit(?L, digit) when digit in [1, 2, 5, "A", "D"], do: digit
  def find_complex_pad_digit(?L, digit) when is_number(digit), do: digit - 1
  def find_complex_pad_digit(?L, "B"), do: "A"
  def find_complex_pad_digit(?L, "C"), do: "B"
end
